import React, { useEffect, useRef, useState } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";

// –†–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–µ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [count, setCount] = useState(null);
  const [history, setHistory] = useState([]);
  const videoRef = useRef(null);

  // 1. –ó–∞–ø—É—Å–∫ –∫–∞–º–µ—Ä—ã
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (e) {
        console.error("–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:", e);
      }
    };
    startCamera();
  }, []);

  // 2. –ê–≤—Ç–æ–¥–µ—Ç–µ–∫—Ç –∫–∞–∂–¥—ã–µ 0.5 —Å
  useEffect(() => {
    const interval = setInterval(handleDetect, 500);
    return () => clearInterval(interval);
    // eslint-disable-next-line
  }, []);

  // 3. –ó–∞–ø—Ä–æ—Å –∫ FastAPI
  const handleDetect = async () => {
    try {
      const res = await fetch("http://localhost:8000/api/v1/detect-live");
      const data = await res.json();
      setCount(data.count);
      // –ù–µ –æ–±–Ω–æ–≤–ª—è–µ–º history —Ç—É—Ç, —á—Ç–æ–±—ã –Ω–µ –¥–µ—Ä–≥–∞—Ç—å –≥—Ä–∞—Ñ–∏–∫ –Ω–∞ –∫–∞–∂–¥—ã–π –∫–∞–¥—Ä!
    } catch (e) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:", e);
    }
  };

  // 4. –ü–æ–¥–≥—Ä—É–∑–∫–∞ –∏—Å—Ç–æ—Ä–∏–∏ –ø–æ—Å–µ—â–∞–µ–º–æ—Å—Ç–∏ —Ä–∞–∑ –≤ 5 —Å–µ–∫
  useEffect(() => {
    const fetchHistory = async () => {
      const res = await fetch("http://localhost:8000/api/v1/attendance-history");
      const data = await res.json();
      setHistory(data);
    };
    fetchHistory();
    const interval = setInterval(fetchHistory, 5000);
    return () => clearInterval(interval);
  }, []);

  // 5. –î–∞–Ω–Ω—ã–µ –¥–ª—è –≥—Ä–∞—Ñ–∏–∫–∞
  const chartData = {
    labels: history.map(h => new Date(h.timestamp).toLocaleTimeString()),
    datasets: [
      {
        label: "–ü–æ—Å–µ—â–∞–µ–º–æ—Å—Ç—å",
        data: history.map(h => h.count),
        fill: false,
        borderColor: "#2563eb",
        tension: 0.1
      }
    ]
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center", fontFamily: "Arial, sans-serif" }}>
      <h1>üéì YOLO Student Counter</h1>
      <div style={{ position: "relative", display: "inline-block" }}>
        <video
          ref={videoRef}
          autoPlay
          muted
          playsInline
          style={{ width: "640px", height: "auto", borderRadius: "10px" }}
        />
      </div>

      {count !== null && (
        <p style={{ marginTop: "1rem", fontSize: "18px" }}>üßç –ù–∞–π–¥–µ–Ω–æ: {count}</p>
      )}

      <div style={{ marginTop: "3rem", background: "#f6f8fa", borderRadius: "12px", padding: "2rem" }}>
        <h2>üìà –ò—Å—Ç–æ—Ä–∏—è –ø–æ—Å–µ—â–∞–µ–º–æ—Å—Ç–∏</h2>
        <Line data={chartData} />
      </div>
    </div>
  );
}

export default App;
