import React, { useState } from "react";

function App() {
  const [count, setCount] = useState(null);
  const [boxes, setBoxes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleDetect = async () => {
    setLoading(true);
    setError("");
    try {
      const response = await fetch("http://localhost:8000/api/v1/detect-live");
      const data = await response.json();
      setCount(data.count);
      setBoxes(data.boxes || []);
      console.log(data);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:", error);
      setError("–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–¥–∫–ª—é—á–∏—Ç—å—Å—è –∫ —Å–µ—Ä–≤–µ—Ä—É.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üéì YOLO Student Counter</h1>

      <button style={styles.button} onClick={handleDetect} disabled={loading}>
        {loading ? "‚è≥ –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ..." : "üé• Detect Live from Camera"}
      </button>

      {error && <p style={styles.error}>‚ö†Ô∏è {error}</p>}

      {count !== null && (
        <p style={styles.count}>üßç –ù–∞–π–¥–µ–Ω–æ —Å—Ç—É–¥–µ–Ω—Ç–æ–≤: <strong>{count}</strong></p>
      )}

      {boxes.length > 0 && (
        <div style={styles.boxList}>
          <h3 style={styles.subTitle}>üì¶ –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã:</h3>
          <ul>
            {boxes.map((box, i) => (
              <li key={i} style={styles.boxItem}>
                #{i + 1}: (xmin: {box.xmin.toFixed(0)}, ymin: {box.ymin.toFixed(0)}, xmax: {box.xmax.toFixed(0)}, ymax: {box.ymax.toFixed(0)})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

// üé® –°—Ç–∏–ª–∏
const styles = {
  container: {
    maxWidth: "700px",
    margin: "0 auto",
    padding: "2rem",
    fontFamily: "Segoe UI, sans-serif",
    backgroundColor: "#f9fafb",
    borderRadius: "12px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
    marginTop: "2rem",
  },
  title: {
    fontSize: "2rem",
    marginBottom: "1.5rem",
    fontWeight: "bold",
    color: "#111827",
  },
  button: {
    padding: "0.75rem 1.5rem",
    fontSize: "1rem",
    fontWeight: "bold",
    backgroundColor: "#2563eb",
    color: "white",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background 0.3s",
    marginBottom: "1rem",
  },
  error: {
    color: "red",
    marginTop: "1rem",
    fontWeight: "bold",
  },
  count: {
    fontSize: "1.25rem",
    marginTop: "1rem",
  },
  boxList: {
    marginTop: "1.5rem",
    backgroundColor: "#e5e7eb",
    padding: "1rem",
    borderRadius: "8px",
  },
  subTitle: {
    fontSize: "1.1rem",
    marginBottom: "0.5rem",
  },
  boxItem: {
    padding: "4px 0",
    fontFamily: "monospace",
  },
};

export default App;
