import React, { useEffect, useRef, useState } from "react";
import { Line, Pie } from "react-chartjs-2";
import "chart.js/auto";

// –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –ø–æ –ø–æ—Å–µ—â–∞–µ–º–æ—Å—Ç–∏
function StatsDashboard() {
  const PERIODS = [
    { key: "hour", label: "–ü–æ —á–∞—Å–∞–º" },
    { key: "day", label: "–ü–æ –¥–Ω—è–º" },
    { key: "week", label: "–ü–æ –Ω–µ–¥–µ–ª—è–º" },
    { key: "month", label: "–ü–æ –º–µ—Å—è—Ü–∞–º" }
  ];
  const [period, setPeriod] = useState("hour");
  const [stats, setStats] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:8000/api/v1/attendance_stats?period=${period}`)
      .then((res) => res.json())
      .then(setStats)
      .catch(() => setStats([]));
  }, [period]);

  const labels = stats.map((row) => row.label);
  const counts = stats.map((row) => row.count);

  const chartData = {
    labels,
    datasets: [
      {
        label: "–ü–æ—Å–µ—â–∞–µ–º–æ—Å—Ç—å",
        data: counts,
        backgroundColor: "rgba(37, 99, 235, 0.4)",
        borderColor: "#2563eb",
        borderWidth: 2,
      },
    ],
  };

  return (
    <div style={{ padding: 24, maxWidth: 900, margin: "0 auto" }}>
      <h2 style={{ marginBottom: 16 }}>üìä –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –ø–æ—Å–µ—â–∞–µ–º–æ—Å—Ç–∏</h2>
      <div style={{ marginBottom: 20 }}>
        {PERIODS.map((item) => (
          <button
            key={item.key}
            onClick={() => setPeriod(item.key)}
            style={{
              marginRight: 10,
              padding: "6px 18px",
              borderRadius: 6,
              border: period === item.key ? "2px solid #2563eb" : "1px solid #ccc",
              background: period === item.key ? "#2563eb" : "#fff",
              color: period === item.key ? "#fff" : "#222",
              fontWeight: 500,
              cursor: "pointer",
            }}
          >
            {item.label}
          </button>
        ))}
      </div>
      <div style={{ display: "flex", gap: 28, flexWrap: "wrap" }}>
        <div style={{ flex: 2, minWidth: 320 }}>
          <Line data={chartData} />
        </div>
        <div style={{ flex: 1, minWidth: 200 }}>
          <Pie data={chartData} />
        </div>
      </div>
      <h3 style={{ marginTop: 36, marginBottom: 12 }}>üìã –û—Ç—á—ë—Ç</h3>
      <table style={{ width: "100%", borderCollapse: "collapse", background: "#f8fafc" }}>
        <thead>
          <tr>
            <th style={{ padding: 8, borderBottom: "1px solid #e5e7eb" }}>–ü–µ—Ä–∏–æ–¥</th>
            <th style={{ padding: 8, borderBottom: "1px solid #e5e7eb" }}>–ö–æ–ª-–≤–æ —Å—Ç—É–¥–µ–Ω—Ç–æ–≤</th>
          </tr>
        </thead>
        <tbody>
          {stats.map((row, i) => (
            <tr key={i}>
              <td style={{ padding: 8, borderBottom: "1px solid #e5e7eb" }}>{row.label}</td>
              <td style={{ padding: 8, borderBottom: "1px solid #e5e7eb" }}>{row.count}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

// Live Detection (–æ—Å–Ω–æ–≤–Ω–æ–π —Ä–µ–∂–∏–º)
function LiveDetection() {
  const [count, setCount] = useState(null);
  const [boxes, setBoxes] = useState([]);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // –ó–∞–ø—É—Å–∫ –∫–∞–º–µ—Ä—ã
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
      } catch (e) {
        console.error("–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:", e);
      }
    };
    startCamera();
  }, []);

  // –ê–≤—Ç–æ–¥–µ—Ç–µ–∫—Ç –∫–∞–∂–¥—ã–µ 0.5 —Å
  useEffect(() => {
    const interval = setInterval(handleDetect, 500);
    return () => clearInterval(interval);
  }, []);

  // –ó–∞–ø—Ä–æ—Å –∫ FastAPI
  const handleDetect = async () => {
    try {
      const res = await fetch("http://localhost:8000/api/v1/detect-live");
      const data = await res.json();
      setCount(data.count);
      setBoxes(data.boxes || []);
    } catch (e) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:", e);
    }
  };

  // –†–∏—Å–æ–≤–∞–Ω–∏–µ –±–æ–∫—Å–æ–≤ (–µ—Å–ª–∏ –Ω—É–∂–Ω–æ, —Å–µ–π—á–∞—Å –æ—Ç–∫–ª—é—á–µ–Ω–æ)
  /*
  useEffect(() => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    const ctx = canvas.getContext("2d");
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    boxes.forEach((box) => {
      const scaleX = canvas.width / 1920;
      const scaleY = canvas.height / 1080;
      ctx.strokeStyle = "red";
      ctx.lineWidth = 2;
      ctx.strokeRect(
        box.xmin * scaleX,
        box.ymin * scaleY,
        (box.xmax - box.xmin) * scaleX,
        (box.ymax - box.ymin) * scaleY
      );
    });
  }, [boxes]);
  */

  return (
    <div style={{ padding: "2rem", textAlign: "center", fontFamily: "Arial, sans-serif" }}>
      <h1>üéì YOLO Student Counter</h1>
      <div style={{ position: "relative", display: "inline-block" }}>
        {/* –í–∏–¥–µ–æ */}
        <video
          ref={videoRef}
          autoPlay
          muted
          playsInline
          style={{ width: "640px", height: "auto" }}
        />
        {/* <canvas ref={canvasRef} style={{ position: "absolute", top: 0, left: 0 }} /> */}
      </div>
      {count !== null && (
        <p style={{ marginTop: "1rem", fontSize: "18px" }}>üßç –ù–∞–π–¥–µ–Ω–æ: {count}</p>
      )}
    </div>
  );
}

// –ì–õ–ê–í–ù–´–ô APP
function App() {
  const [mode, setMode] = useState("live");

  return (
    <div>
      <header style={{ padding: "1.3rem 0 1rem 0", background: "#f3f4f6", marginBottom: 20 }}>
        <div style={{ maxWidth: 1000, margin: "0 auto", display: "flex", gap: 10 }}>
          <button
            onClick={() => setMode("live")}
            style={{
              padding: "8px 24px",
              marginRight: 8,
              borderRadius: 6,
              border: mode === "live" ? "2px solid #2563eb" : "1px solid #ccc",
              background: mode === "live" ? "#2563eb" : "#fff",
              color: mode === "live" ? "#fff" : "#222",
              fontWeight: 500,
              fontSize: 18,
              cursor: "pointer",
            }}
          >
            Live Detection
          </button>
          <button
            onClick={() => setMode("stats")}
            style={{
              padding: "8px 24px",
              borderRadius: 6,
              border: mode === "stats" ? "2px solid #2563eb" : "1px solid #ccc",
              background: mode === "stats" ? "#2563eb" : "#fff",
              color: mode === "stats" ? "#fff" : "#222",
              fontWeight: 500,
              fontSize: 18,
              cursor: "pointer",
            }}
          >
            –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
          </button>
        </div>
      </header>
      {mode === "live" ? <LiveDetection /> : <StatsDashboard />}
    </div>
  );
}

export default App;
