import React, { useEffect, useRef, useState } from "react";

function App() {
  const [count, setCount] = useState(null);
  const [boxes, setBoxes] = useState([]);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error("–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:", error);
      }
    };
    startCamera();
  }, []);

  const handleDetect = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/v1/detect-live");
      const data = await response.json();
      setCount(data.count);
      setBoxes(data.boxes || []);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:", error);
    }
  };

  useEffect(() => {
    const drawBoxes = () => {
      const canvas = canvasRef.current;
      const video = videoRef.current;
      const ctx = canvas.getContext("2d");

      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      boxes.forEach((box) => {
        const scaleX = canvas.width / 1920;
        const scaleY = canvas.height / 1080;

        ctx.strokeStyle = "red";
        ctx.lineWidth = 2;
        ctx.strokeRect(
          box.xmin * scaleX,
          box.ymin * scaleY,
          (box.xmax - box.xmin) * scaleX,
          (box.ymax - box.ymin) * scaleY
        );
      });
    };

    if (videoRef.current?.videoWidth && boxes.length > 0) {
      drawBoxes();
    }
  }, [boxes]);

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif", textAlign: "center" }}>
      <h1>üéì –†–∞–∑—Ä–∞–±–æ—Ç–∫–∞ —Å–∏—Å—Ç–µ–º—ã –ø–æ–¥—Å—á–µ—Ç–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ —Å—Ç—É–¥–µ–Ω—Ç–æ–≤ –≤ –∞—É–¥–∏—Ç–æ—Ä–∏—è—Ö</h1>
      <div style={{ position: "relative", display: "inline-block" }}>
        <video ref={videoRef} autoPlay muted playsInline style={{ width: "640px" }} />
        <canvas ref={canvasRef} style={{ position: "absolute", top: 0, left: 0 }} />
      </div>

      <div style={{ marginTop: "1rem" }}>
        <button
          onClick={handleDetect}
          style={{
            padding: "10px 20px",
            backgroundColor: "#2563eb",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          üé• –û–±–Ω–∞—Ä—É–∂–∏—Ç—å —Å—Ç—É–¥–µ–Ω—Ç–æ–≤
        </button>
      </div>

      {count !== null && (
        <p style={{ marginTop: "1rem", fontSize: "18px" }}>üßç –ù–∞–π–¥–µ–Ω–æ: {count}</p>
      )}
    </div>
  );
}

export default App;
