import React, { useEffect, useRef, useState } from "react";

function App() {
  const [count, setCount] = useState(null);
  const [boxes, setBoxes] = useState([]);
  const videoRef = useRef(null);

  // –ó–∞–ø—É—Å–∫–∞–µ–º –∫–∞–º–µ—Ä—É –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: true,
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error("–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:", error);
      }
    };

    startCamera();
  }, []);

  // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –¥–µ—Ç–µ–∫—Ü–∏–∏ –∫–∞–∂–¥—ã–µ 5 —Å–µ–∫—É–Ω–¥
  useEffect(() => {
    const interval = setInterval(() => {
      handleDetect();
    }, 1000); // 5000 –º—Å = 5 —Å–µ–∫—É–Ω–¥

    return () => clearInterval(interval); // –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  }, []);

  const handleDetect = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/v1/detect-live");
      const data = await response.json();
      setCount(data.count);
      setBoxes(data.boxes || []);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:", error);
    }
  };

  return (
    <div
      style={{
        padding: "2rem",
        fontFamily: "Arial, sans-serif",
        maxWidth: "800px",
        margin: "auto",
      }}
    >
      <h1>
        üéì <b>YOLO Student Counter</b>
      </h1>

      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        style={{ width: "100%", borderRadius: "10px", marginBottom: "1rem" }}
      />

      <button
        onClick={handleDetect}
        style={{
          padding: "10px 20px",
          backgroundColor: "#2563eb",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        üé• –û–±–Ω–æ–≤–∏—Ç—å –≤—Ä—É—á–Ω—É—é
      </button>

      {count !== null && (
        <div style={{ marginTop: "1rem", fontSize: "18px" }}>
          üßç –ù–∞–π–¥–µ–Ω–æ —Å—Ç—É–¥–µ–Ω—Ç–æ–≤: <b>{count}</b>
        </div>
      )}

      {boxes.length > 0 && (
        <div
          style={{
            backgroundColor: "#f3f4f6",
            padding: "1rem",
            marginTop: "1rem",
            borderRadius: "8px",
          }}
        >
          <b>üì¶ –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã:</b>
          <ul>
            {boxes.map((box, index) => (
              <li key={index}>
                #{index + 1}: (xmin: {Math.round(box.xmin)}, ymin:{" "}
                {Math.round(box.ymin)}, xmax: {Math.round(box.xmax)}, ymax:{" "}
                {Math.round(box.ymax)})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
