import React, { useState, useEffect, useRef } from "react";

function App() {
  const [count, setCount] = useState(null);
  const [boxes, setBoxes] = useState([]);
  const videoRef = useRef(null);

  // ‚úÖ –ü–æ–ª—É—á–µ–Ω–∏–µ –≤–∏–¥–µ–æ–ø–æ—Ç–æ–∫–∞ —Å –∫–∞–º–µ—Ä—ã
  useEffect(() => {
    const getCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
      } catch (err) {
        console.error("–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:", err);
      }
    };
    getCamera();
  }, []);

  // üß† –í—ã–∑–æ–≤ –±–µ–∫–µ–Ω–¥–∞
  const handleDetect = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/v1/detect-live");
      const data = await response.json();
      setCount(data.count);
      setBoxes(data.boxes || []);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:", error);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h1>üéì YOLO Student Counter</h1>

      {/* ‚ñ∂Ô∏è –í–∏–¥–µ–æ —Å –∫–∞–º–µ—Ä—ã */}
      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        width="640"
        height="480"
        style={{ border: "2px solid #ccc", borderRadius: "8px", marginBottom: "1rem" }}
      />

      <br />

      <button onClick={handleDetect} style={{ padding: "0.5rem 1rem" }}>
        üì∏ Detect Live from Camera
      </button>

      {count !== null && (
        <div style={{ marginTop: "1rem" }}>
          <h2>üßç –ù–∞–π–¥–µ–Ω–æ —Å—Ç—É–¥–µ–Ω—Ç–æ–≤: <strong>{count}</strong></h2>
        </div>
      )}

      {boxes.length > 0 && (
        <div style={{ marginTop: "1rem", background: "#f2f2f2", padding: "1rem", borderRadius: "8px" }}>
          <h3>üì¶ –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã:</h3>
          <ul>
            {boxes.map((box, idx) => (
              <li key={idx}>
                #{idx + 1}: (xmin: {Math.round(box.xmin)}, ymin: {Math.round(box.ymin)}, xmax: {Math.round(box.xmax)}, ymax: {Math.round(box.ymax)})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;

useEffect(() => {
  const interval = setInterval(() => {
    handleDetect();
  }, 3000); // –∫–∞–∂–¥—ã–µ 3 —Å–µ–∫—É–Ω–¥—ã
  return () => clearInterval(interval);
}, []);
